mapper.select:
select 

from module_info
where module_no = #{moduleNo,jdbcType=VARCHAR}
  and parent_module_no = #{parentModuleNo,jdbcType=VARCHAR}

mapper.delete:
delete from module_info
where module_no = #{moduleNo,jdbcType=VARCHAR}
  and parent_module_no = #{parentModuleNo,jdbcType=VARCHAR}

mapper.insert:
insert into module_info (module_no, parent_module_no, module_name, 
  description, display_order, create_user, 
  create_date, update_user, update_date
  )
values (#{moduleNo,jdbcType=VARCHAR}, #{parentModuleNo,jdbcType=VARCHAR}, #{moduleName,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{displayOrder,jdbcType=INTEGER}, #{createUser,jdbcType=INTEGER}, 
  #{createDate,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=INTEGER}, #{updateDate,jdbcType=TIMESTAMP}
  )

mapper.update:
update module_info
set module_name = #{moduleName,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR},
  display_order = #{displayOrder,jdbcType=INTEGER},
  create_user = #{createUser,jdbcType=INTEGER},
  create_date = #{createDate,jdbcType=TIMESTAMP},
  update_user = #{updateUser,jdbcType=INTEGER},
  update_date = #{updateDate,jdbcType=TIMESTAMP}
where module_no = #{moduleNo,jdbcType=VARCHAR}
  and parent_module_no = #{parentModuleNo,jdbcType=VARCHAR}
